// Goroutine は Go ランタイムによって管理される軽量スレッド

// Goroutine を開始
go func() {
  // コードは並行して実行される
  fmt.Println("Running in goroutine")
}()

// 関数を Goroutine で実行
go myFunction(arg1, arg2)

// Main は実行を継続
fmt.Println("Main function continues")

// Goroutine の終了を待つ (最も簡単な方法)
time.Sleep(time.Second)  // 本番環境では信頼できない

// ベストプラクティス: 同期プリミティブを使用する
var wg sync.WaitGroup

wg.Add(1)  // 1つの Goroutine のカウンターを追加
go func() {
  defer wg.Done()  // Goroutine 終了時にカウンターをデクリメント
  // 処理を実行...
}()

wg.Wait()  // カウンターがゼロになるまで待機

// 複数の Goroutine を作成
for i := 0; i < 5; i++ {
  i := i  // クロージャの問題を避けるために新しい変数を作成
  wg.Add(1)
  go func() {
    defer wg.Done()
    fmt.Println("Goroutine", i)
  }()
}
wg.Wait()

// チャネルは Goroutine 間の通信のための型付きパイプ

// チャネルを作成
ch := make(chan int)       // バッファなし
buffCh := make(chan int, 10)  // バッファあり (capacity 10)

// チャネルに送信 (矢印はチャネルを指す)
ch <- 42  // バッファなしチャネル、またはバッファありチャネルが満杯の場合にブロックする

// チャネルから受信 (矢印はチャネルから出る)
value := <-ch  // チャネルが空の場合にブロックする

// チャネルを閉じる (送信側が閉じるべき)
close(ch)

// チャネルが閉じているか確認
value, ok := <-ch  // チャネルが閉じていて空の場合、ok は false

// チャネルを range で処理 (チャネルが閉じられると終了)
for value := range ch {
  // 値を処理
}

// 方向性チャネル (型制限)
func producer(ch chan<- int) {  // チャネルへの送信のみ可能
  ch <- 42
}

func consumer(ch <-chan int) {  // チャネルからの受信のみ可能
  value := <-ch
}

// チャネルパターン
// ワーカープール
func worker(id int, jobs <-chan int, results chan<- int) {
  for j := range jobs {
    results <- j * 2  // 処理を実行
  }
}

jobs := make(chan int, 100)
results := make(chan int, 100)

// 3つのワーカーを開始
for w := 1; w <= 3; w++ {
  go worker(w, jobs, results)
}

// ジョブを送信し、結果を収集
for j := 1; j <= 9; j++ {
  jobs <- j
}
close(jobs)

// Select 文は複数のチャネル操作を待機できる

select {
case value := <-ch1:
  // ch1 からの値を取り扱う
case ch2 <- value:
  // ch2 に値を送信した
case <-time.After(1 * time.Second):
  // 1秒後にタイムアウト
case <-done:
  // done チャネルが閉じられた、終了
  return
default:
  // 準備ができているチャネルがない場合に実行 (ノンブロッキング)
}

// 一般的なパターン

// ノンブロッキングチャネル操作
select {
case x := <-ch:
  // x を使用
default:
  // チャネル準備未完了
}

// タイムアウト
select {
case res := <-ch:
  // res を使用
case <-time.After(time.Second):
  // 1秒後にタイムアウトした
}

// 終了チャネル
func worker(ch <-chan int, quit <-chan bool) {
  for {
    select {
    case value := <-ch:
      // 値を処理
    case <-quit:
      fmt.Println("Quitting")
      return
    }
  }
}

// 多方向 select ループ
for {
  select {
  case <-ch1:
    // イベント1を処理
  case <-ch2:
    // イベント2を処理
  case <-done:
    return
  }
}

// 共有データへの安全な並行アクセスのための相互排他
import "sync"

// 基本的な mutex の使用法
var (
  mu    sync.Mutex
  count int
)

func increment() {
  mu.Lock()      // 共有データアクセス前にロック
  defer mu.Unlock()  // アンロックが確実に行われるようにする
  count++
}

// 読み書き mutex (複数のリーダーまたは1つのライター)
var rwmu sync.RWMutex

func readData() {
  rwmu.RLock()    // 複数の Goroutine が読み取りロックを保持できる
  defer rwmu.RUnlock()
  // 共有データを読み取る...
}

func writeData() {
  rwmu.Lock()     // 書き込みのための排他ロック
  defer rwmu.Unlock()
  // 共有データを変更する...
}

// Once - 厳密に一度だけ実行
var once sync.Once

func initialize() {
  once.Do(func() {
    // 初期化コード、厳密に一度だけ実行される
  })
}

// WaitGroup - Goroutine のコレクションを待機
var wg sync.WaitGroup

wg.Add(3)  // カウンターを3に設定
go func() {
  defer wg.Done()  // カウンターをデクリメント
  // 処理 1
}()
// ... さらに Goroutine を起動
wg.Wait()  // カウンターがゼロになるまでブロック

// Cond - イベント待機のための条件変数
cond := sync.NewCond(&sync.Mutex{})

// ブロードキャスト
cond.L.Lock()
// 状態を変更
cond.Broadcast()  // すべての待機者を起こす
cond.L.Unlock()

// 待機中
cond.L.Lock()
for !condition() {
  cond.Wait()  // アンロックし、待機し、再度ロックする
}
// 条件を使用
cond.L.Unlock()

// 低レベル同期のためのアトミック操作
import "sync/atomic"

var counter atomic.Int64  // Go 1.19+

// 値を追加して取得
counter.Add(10)
value := counter.Load()

// 比較して交換
swapped := counter.CompareAndSwap(10, 20)  // 値が 10 なら 20 に設定

// 値を格納
counter.Store(100)

// In Go <1.19
var counter int64

// 追加して取得
atomic.AddInt64(&counter, 10)
value := atomic.LoadInt64(&counter)

// 比較して交換
swapped := atomic.CompareAndSwapInt64(&counter, 10, 20)

// 値を格納
atomic.StoreInt64(&counter, 100)

// アトミックポインタ操作
var ptr atomic.Pointer[T]  // Go 1.19+
ptr.Store(&myStruct)
value := ptr.Load()
swapped := ptr.CompareAndSwap(oldPtr, newPtr)

// Context はデッドライン、キャンセルシグナル、リクエストスコープの値を運ぶ
import "context"

// Context の作成
// Background context (すべての context のルート)
ctx := context.Background()

// WithCancel - キャンセル可能な context
ctx, cancel := context.WithCancel(context.Background())
defer cancel()  // 完了時にキャンセル

// 時間制限付き context
ctx, cancel := context.WithTimeout(context.Background(), time.Second)
defer cancel()

ctx, cancel := context.WithDeadline(context.Background(), time.Now().Add(time.Minute))
defer cancel()

// Context 値 (リクエストスコープデータ用)
ctx = context.WithValue(ctx, "key", "value")
value := ctx.Value("key").(string)

// 関数での context の使用
func doWork(ctx context.Context) error {
  select {
  case <-time.After(100 * time.Millisecond):
    return nil
  case <-ctx.Done():
    return ctx.Err()  // context.Canceled or context.DeadlineExceeded
  }
}

// キャンセル
ctx, cancel := context.WithCancel(context.Background())
go func() {
  time.Sleep(time.Second)
  cancel()  // キャンセルを通知
}()
doWork(ctx)
