codeExamples.code:
// Basic if-else
if x > 10 {
  // do something
} else if x > 5 {
  // do something else
} else {
  // do another thing
}

// If with short statement
if value := getValue(); value > 10 {
  // use value
}

// Standard for loop
for i := 0; i < 10; i++ {
  // loop body
}

// For as while
for condition {
  // loop body
}

// Infinite loop
for {
  // loop body
  if shouldBreak {
    break
  }
}

// For-range loop
for index, value := range collection {
  // use index and value
}

// Basic switch
switch value {
case 1:
  // code for 1
case 2, 3:
  // code for 2 or 3
default:
  // default code
}

// Switch with no expression
switch {
case x > 100:
  // code for x > 100
case x > 10:
  // code for x > 10
default:
  // default code
}
