codeExamples.code:
// ファイル: math.go
package math

// エクスポートされた関数 (大文字で始まる)
func Add(a, b int) int {
  return a + b
}

// エクスポートされない関数 (小文字で始まる)
func multiply(a, b int) int {
  return a * b
}

// パッケージドキュメント
// package 宣言の前のこのコメントは
// godoc でパッケージドキュメントになる

// Package math は基本的な数学的操作を提供する。
package math

// 単一インポート
import "fmt"

// 複数インポート
import (
  "fmt"
  "math"
  "strings"
)

// エイリアス付きインポート
import (
  f "fmt"       // f.Println()
  m "math"      // m.Pi
  . "math"      // Pi (直接アクセス、本番コードでは避ける)
  _ "image/png" // PNG デコーダを登録するが、直接は使用しない
)

// ドットインポート (非推奨)
import . "math"
fmt.Println(Pi) // パッケージ名なしで直接アクセス

// ブランクインポート (副作用のためだけに使用)
import _ "net/http/pprof" // プロファイリングハンドラを登録

// 相対インポート (go modules 内)
import (
  "example.com/project/pkg/util"  // 標準インポート
  "./mylocal"                     // modules では無効
)

package mypackage

// パッケージ変数
var PackageVar int

// init 関数はパッケージがインポートされるときに実行される
// main() が開始する前に
func init() {
  PackageVar = 42
  // 初期化コード
}

// 複数の init 関数は宣言順に実行される
func init() {
  // 最初の init
}

func init() {
  // 2番目の init
}

func DoSomething() {
  // 関数コード
}

// パッケージ初期化順序:
// 1. パッケージレベルの変数初期化
// 2. 宣言順の init() 関数

// パッケージ間:
// 1. インポートされたパッケージが最初に初期化される
// 2. 次にインポート元のパッケージが初期化される

// 主要な標準ライブラリパッケージ

// フォーマット
import "fmt"
fmt.Println("Hello, world")
fmt.Printf("%d %s\n", 42, "text")

// 入出力
import "io"
import "os"
file, err := os.Open("file.txt")
data, err := io.ReadAll(file)

// 文字列
import "strings"
s := strings.ToUpper("hello")
contains := strings.Contains(s, "EL")
parts := strings.Split("a,b,c", ",")

// 時間
import "time"
now := time.Now()
time.Sleep(time.Second * 2)
duration := time.Since(now)

// JSON
import "encoding/json"
type Person struct {
  Name string `json:"name"`
  Age  int    `json:"age"`
}
data, err := json.Marshal(person)
person := Person{}
err := json.Unmarshal(data, &person)

// HTTP
import "net/http"
http.HandleFunc("/hello", func(w http.ResponseWriter, r *http.Request) {
  fmt.Fprintf(w, "Hello")
})
http.ListenAndServe(":8080", nil)

// internal パッケージは、親パッケージまたは
// 兄弟パッケージからのみインポート可能

// ディレクトリ構造:
// myproject/
//   cmd/
//     app/
//       main.go  // "myproject/internal/auth" をインポート可能
//   internal/
//     auth/
//       auth.go
//   pkg/
//     public/
//       public.go  // "myproject/internal/auth" をインポート可能

// auth.go 内
package auth

func Authenticate(user, pass string) bool {
  // 実装
}

// main.go 内
package main

import "myproject/internal/auth"

func main() {
  if auth.Authenticate("user", "pass") {
    // ...
  }
}

// 外部パッケージは "myproject/internal/..." をインポートできない
// これは Go コンパイラによって強制される
