// File: math.go
package math

// Exported function (capitalized)
func Add(a, b int) int {
  return a + b
}

// Unexported function (lowercase)
func multiply(a, b int) int {
  return a * b
}

// Single import
import "fmt"

// Multiple imports
import (
  "fmt"
  "math"
  "strings"
)

// Import with alias
import (
  f "fmt"
  m "math"
)

// Using the imports
f.Println(m.Pi)

package mypackage

// Package variables
var PackageVar int

// init function runs when package is imported
func init() {
  PackageVar = 42
  // initialization code
}

func DoSomething() {
  // function code
}
