codeExamples.code:
// エクスポートされた関数 (大文字で始まる)
func Add(a, b int) int {
  return a + b
}

// エクスポートされない関数 (小文字で始まる)
func multiply(a, b int) int {
  return a * b
}

// パッケージドキュメント
// package 宣言の前のこのコメントは
// godoc でパッケージドキュメントになる

// Package math は基本的な数学的操作を提供する。
package math

// 単一インポート
import "fmt"

// 複数インポート
import (
  "fmt"
  "math"
  "strings"
)

// エイリアス付きインポート
import (
// f.Println()
  f "fmt"
// m.Pi
  m "math"
)

// ドットインポート (非推奨)
import . "math"
// パッケージ名なしで直接アクセス
fmt.Println(Pi)

// ブランクインポート (副作用のためだけに使用)
// PNG デコーダを登録するが、直接は使用しない
import _ "image/png"
// プロファイリングハンドラを登録
import _ "net/http/pprof"

// 相対インポート (go modules 内)
import (
// 標準インポート
  "example.com/project/pkg/util"
// modules では無効
//  "./mylocal"
)

package mypackage

// パッケージ変数
var PackageVar int

// init 関数はパッケージがインポートされるときに実行される
// main() が開始する前に
func init() {
  PackageVar = 42
  // 初期化コード
}

// 複数の init 関数は宣言順に実行される
func init() {
  // 最初の init
}

func init() {
  // 2番目の init
}

// パッケージ初期化順序:
// 1. パッケージレベルの変数初期化
// 2. 宣言順の init() 関数

// パッケージ間:
// 1. インポートされたパッケージが最初に初期化される
// 2. 次にインポート元のパッケージが初期化される

// モジュール初期化
// 新しいモジュールを作成
go mod init example.com/mymodule

// モジュールファイル (go.mod)
module example.com/mymodule

// 最小 Go バージョン
go 1.19

require (
  github.com/pkg/errors v0.9.1
  golang.org/x/text v0.9.0
)

exclude github.com/unwanted/package v1.0.0

replace github.com/original/package => github.com/fork/package v1.2.0

// 依存関係の管理
// 依存関係を追加
go get github.com/pkg/errors
// 特定のバージョン
go get github.com/pkg/errors@v0.9.1
// すべての依存関係を更新
go get -u
// 未使用の依存関係をクリーンアップ
go mod tidy
// vendor ディレクトリを作成
go mod vendor
// 依存関係を説明
go mod why github.com/pkg/errors
// すべての依存関係をリスト
go list -m all

// JSON
import "encoding/json"
type Person struct {
  Name string `json:"name"`
  Age  int    `json:"age"`
}
data, err := json.Marshal(person)
person := Person{}
err := json.Unmarshal(data, &person)

// HTTP
import "net/http"
http.HandleFunc("/hello", func(w http.ResponseWriter, r *http.Request) {
  fmt.Fprintf(w, "Hello")
})
http.ListenAndServe(":8080", nil)

// internal パッケージは、親パッケージまたは
// 兄弟パッケージからのみインポート可能

// ディレクトリ構造:
// myproject/
//   cmd/
//     app/
//       main.go  // "myproject/internal/auth" をインポート可能
//   internal/
//     auth/
//       auth.go
//   pkg/
//     public/
//       public.go  // "myproject/internal/auth" をインポート可能

// auth.go 内
package auth

func Authenticate(user, pass string) bool {
  // 実装
}

// main.go 内
package main

import "myproject/internal/auth"

func main() {
  if auth.Authenticate("user", "pass") {
    // ...
  }
}

// 外部パッケージは "myproject/internal/..." をインポートできない
// これは Go コンパイラによって強制される
