// Go テストパターン
// 別のテストパッケージ
package mypackage_test

import (
  "testing"

  "example.com/mypackage"
)

// 基本的なテスト関数
func TestAdd(t *testing.T) {
  got := mypackage.Add(2, 3)
  want := 5
  if got != want {
    t.Errorf("Add(2, 3) = %d; want %d", got, want)
  }
}

// テーブル駆動テスト
func TestAdd_Table(t *testing.T) {
  tests := []struct {
    name string
    a, b int
    want int
  }{
    {"positive", 2, 3, 5},
    {"negative", -1, -2, -3},
    {"mixed", -1, 5, 4},
  }

  for _, tt := range tests {
    t.Run(tt.name, func(t *testing.T) {
      got := mypackage.Add(tt.a, tt.b)
      if got != tt.want {
        t.Errorf("Add(%d, %d) = %d; want %d", tt.a, tt.b, got, tt.want)
      }
    })
  }
}

// ベンチマーク
func BenchmarkAdd(b *testing.B) {
  for i := 0; i < b.N; i++ {
    mypackage.Add(2, 3)
  }
}

// テストの実行:
// $ go test
// $ go test -v
// $ go test -run=TestAdd
// $ go test -bench=.
// $ go test -cover

// テスト可能な例 (ドキュメントとテスト)
func ExampleAdd() {
  sum := mypackage.Add(1, 2)
  fmt.Println(sum)
  // Output: 3
}

// モック
type mockUserStore struct {
  users map[string]*User
}

func (m *mockUserStore) GetUser(id string) (*User, error) {
  user, ok := m.users[id]
  if !ok {
    return nil, errors.New("user not found")
  }
  return user, nil
}

// モックを使用したテスト
func TestHandler_GetUser(t *testing.T) {
  // モックを設定
  store := &mockUserStore{
    users: map[string]*User{
      "123": {ID: "123", Name: "Test User"},
    },
  }

  // モックを注入
  handler := NewUserHandler(store)

  // テスト
  user, err := handler.GetUser("123")
  if err != nil {
    t.Fatalf("expected no error, got %v", err)
  }
  if user.Name != "Test User" {
    t.Errorf("expected name 'Test User', got '%s'", user.Name)
  }
}

// 関数オプションパターン
type ServerOption func(*Server)

func WithTimeout(timeout time.Duration) ServerOption {
  return func(s *Server) {
    s.timeout = timeout
  }
}

func WithTLS(cert, key string) ServerOption {
  return func(s *Server) {
    s.useTLS = true
    s.cert = cert
    s.key = key
  }
}

func NewServer(addr string, opts ...ServerOption) *Server {
  // デフォルト設定
  server := &Server{
    addr:    addr,
    timeout: 30 * time.Second,
  }

  // オプションを適用
  for _, opt := range opts {
    opt(server)
  }

  return server
}

// 使用法
server := NewServer(":8080",
  WithTimeout(10*time.Second),
  WithTLS("cert.pem", "key.pem"),
)

// ワーカープールパターン
func worker(id int, jobs <-chan Job, results chan<- Result) {
  for job := range jobs {
    results <- process(job)
  }
}

// プールを作成
jobs := make(chan Job, 100)
results := make(chan Result, 100)

// ワーカーを開始
for w := 1; w <= 3; w++ {
  go worker(w, jobs, results)
}

// ジョブを送信し、結果を収集
for _, j := range allJobs {
  jobs <- j
}
close(jobs)

// 結果を収集
for range allJobs {
  result := <-results
  // 結果を使用
}
